usuariosUnicos = []
localizacaoUsuario = []
movimento = []

'''Leitura do Arquivo'''
    
def obterMovimentos(valor):
    
    arquivo = open('segunda_0'+str(valor)+'.csv','r')
    tipoAssociation = arquivo.readlines()
    
    '''Quebra da linha do arquivo'''
    
    linhaArquivo = []
    
    for linha in range(len(tipoAssociation)):
        requisicao = tipoAssociation[linha]
        reqSeparada = requisicao.split(",")
        linhaArquivo.append(reqSeparada)
    
    #Obter centroAtual(Destination address) e user(Source address) na linha do arquivo
        macDestination = linhaArquivo[linha][7][1:-1]
        if str(macDestination) in listaTeste:
            posicaodoMac = listaTeste.index(macDestination)
            centroAtual = listaTeste[posicaodoMac+1]
            user = linhaArquivo[linha][6][1:-1]
        #Se o usuario da requisicao nao estiver na lista de usuarios, sera adicionado e tera a localizacao atualizada    
            if user not in usuariosUnicos:
                usuariosUnicos.append(user)
                localizacaoUsuario.append(centroAtual)
            else:
        #Pega a localizacao antiga do usuario da requisicao
                centroAntigo = localizacaoUsuario[usuariosUnicos.index(user)]
        #Se o usuario da requisicao estiver na lista de usuarios, pega a localizacao atual e confere se e igual a antiga, se for diferente contabiliza um movimento
                if centroAntigo != centroAtual:
                    movimento.append(centroAntigo+' '+centroAtual+'\n')
    arquivo.close()         
        
arquivo2 = open('dados_segunda.txt','a')
for xp in range(len(movimento)):
    arquivo2.write(movimento[xp])
arquivo2.close() 